export const AddProduct = (value) => async dispatch =>{                        
        dispatch({
            type:"ADD",                                        //Action Dispatch    -> Action => based on Type And Payload
            payload: value                            //what's the value you are enter
        })
}
export const RemoveProduct = (value) => async dispatch =>{
    dispatch({
        type:"REMOVE",
        payload: {name:value}
    })
}
